void Screen1ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &RAZX_buttonWithLabel)
    {
        //RAZ_X_Interaction
        //When RAZX_buttonWithLabel clicked call virtual function
        //Call raz_X
        raz_X();
    }
    if (&src == &RAZY_buttonWithLabel)
    {
        //RAZ_Y_Interaction
        //When RAZY_buttonWithLabel clicked call virtual function
        //Call raz_y
        raz_y();
    }
    if (&src == &plus_buttonWithLabel)
    {
        //ADD_XY_Interaction
        //When plus_buttonWithLabel clicked call virtual function
        //Call add_XY
        add_XY();
    }
    if (&src == &minus_buttonWithLabel)
    {
        //SUB_XY_Interaction
        //When minus_buttonWithLabel clicked call virtual function
        //Call sub_XY
        sub_XY();
    }
    if (&src == &mult_buttonWithLabel)
    {
        //MUL_XY_Interaction
        //When mult_buttonWithLabel clicked call virtual function
        //Call mul_XY
        mul_XY();
    }
    if (&src == &div_buttonWithLabel)
    {
        //DIV_XY_Interaction
        //When div_buttonWithLabel clicked call virtual function
        //Call div_XY
        div_XY();
    }
    if (&src == &ok_buttonWithLabel)
    {
        //OK_Interaction
        //When ok_buttonWithLabel clicked call virtual function
        //Call ok_function
        ok_function();
    }
}

